mapper.select:
select * from user where 1 = 1

    and username like '%${username}%'


    and roleId = #{roleId}


    and sex = #{sex}


    and areaId = #{areaId}


    limit #{offset},#{pageSize}

select count(*) from user where 1 = 1

    and username like '%${username}%'


    and roleId = #{roleId}


    and sex = #{sex}


    and areaId = #{areaId}
