a.areaName 地区 , COUNT(*) 老年人总数, (select COUNT(*) from oldpeo o JOIN healthcheck ch on o.recordId=ch.userinfoId) 老年人体检总次数 , (SELECT COUNT(*) from oldpeo o JOIN healthcheck ch on o.recordId=ch.userinfoId where ch.healthcheckAcpEcg != 0) 心电图体检次数, (SELECT COUNT(*) from oldpeo o JOIN healthcheck ch on o.recordId=ch.userinfoId where ch.healthcheckProblemaCered != 0) 脑血管体检次数, (SELECT COUNT(*) from oldpeo o JOIN healthcheck ch on o.recordId=ch.userinfoId where ch.healthcheckProblemaKidneyd != 0) 肾脏体检次数, (SELECT COUNT(*) from oldpeo o JOIN healthcheck ch on o.recordId=ch.userinfoId where ch.healthcheckProblemaHeartd != 0) 心脏体检次数, (SELECT COUNT(*) from oldpeo o JOIN healthcheck ch on o.recordId=ch.userinfoId where ch.healthcheckProblemaVarsculard != 0) 血管体检次数, (SELECT COUNT(*) from oldpeo o JOIN healthcheck ch on o.recordId=ch.userinfoId where ch.healthcheckAcpBtypeultrasonic != 0) 腹部B超检查次数, (select count(*) from (select otc.* from oldtcm otc, oldpeo peo,userinfo u,area a where otc.oldpeoId=peo.oldpeoId and peo.recordId=u.userId and a.areaId=u.recordUnit
and 1=1
and u.recordUnit=#{areaId}
) ds) 中医药服务次数 from userinfo u Join area a on a.areaId=u.recordUnit Join oldpeo op on op.recordId=u.userId where 1=1
and 1=1
and a.areaId=#{areaId}
) ss
and username like '%${username}%'
