mapper.select:
select u.userNumber,u.userIdnumber,u.recordName,u.userSex,u.userBirthday,
u.recordAdress,u.userMyphone,u.recordData ,o.oldpeoMstate,u.userMyphone
from userinfo u ,oldpeo o where 1=1 and o.recordId=u.userId

    and username like '%${username}%'


    and roleId = #{roleId}


    and sex = #{sex}


    limit #{offset},#{pageSize}

select count(*) from oldpeo where 1 = 1

    and username like '%${username}%'


    and roleId = #{roleId}


    and sex = #{sex}

select  u.userId,u.recordName,u.userBirthday,u.recordAdress,u.userMyphone
from userinfo u,oldpeo where u.userId!=oldpeo.recordId

    limit #{offset},#{pageSize}
