mapper.select:
select p.pultubfoId,p.userId,u.recordName,u.userSex,u.userBirthday,u.recordAdress,u.userMyphone,y.userPrivyPhone,p.puljkTime,p.pulState,p.pulDoctor from userinfo u join pultubfo p on u.userId=p.userId left join userPrivy y on u.userId=y.userId
where 1 = 1

    and username like '%${username}%'


    and roleId = #{roleId}


    and sex = #{sex}


    limit #{offset},#{pageSize}

select count(*) from pultubfo where 1 = 1

    and username like '%${username}%'


    and roleId = #{roleId}


    and sex = #{sex}
