mapper.select:
select p.patrolId,p.areaId,a.areaName,p.patrolYear,p.patrolContent,p.patrolProblem,p.patrolTime,p.patrolName,p.patrolRemarks,p.patrolState,p.patrolColumn from supmanPatrol p join area a on p.areaId=a.areaId
where 1=1


            and patrolName like '%${patrolName}%'


            and p.patrolTime  >= #{beginTime}


            and   #{endTime} >= p.patrolTime


        limit #{offset},#{pageSize}


    /*order by p.patrolTime desc*/

select count(*) from supmanPatrol
where 1=1


    and patrolName like '%${patrolName}%'


    and patrolTime  >= #{beginTime}


    and   #{endTime} >= patrolTime


    limit #{offset},#{pageSize}

/*order by patrolTime desc*/
